/**
 * @internal
 * wrapper
 */
static ipCidrRouteTable_rowreq_ctx*
_mfd_ipCidrRouteTable_rowreq_from_index( Types_Index* oid_idx,
    int* rc_ptr )
{
    ipCidrRouteTable_rowreq_ctx* rowreq_ctx;
    ipCidrRouteTable_mib_index mib_idx;
    int rc;

    DEBUG_MSGTL( ( "internal:ipCidrRouteTable:_mfd_ipCidrRouteTable_rowreq_from_index", "called\n" ) );

    if ( NULL == rc_ptr )
        rc_ptr = &rc;
    *rc_ptr = MFD_SUCCESS;

    memset( &mib_idx, 0x0, sizeof( mib_idx ) );

    /*
     * try to parse oid
     */
    *rc_ptr = ipCidrRouteTable_index_from_oid( oid_idx, &mib_idx );
    if ( MFD_SUCCESS != *rc_ptr ) {
        DEBUG_MSGT( ( "ipCidrRouteTable", "error parsing index\n" ) );
        return NULL;
    }

    /*
     * allocate new context
     */
    rowreq_ctx = ipCidrRouteTable_allocate_rowreq_ctx( NULL, NULL );
    if ( NULL == rowreq_ctx ) {
        *rc_ptr = MFD_ERROR;
        return NULL; /* msg already logged */
    }

    memcpy( &rowreq_ctx->tbl_idx, &mib_idx, sizeof( mib_idx ) );

    /*
     * check indexes
     */
    *rc_ptr = _ipCidrRouteTable_check_indexes( rowreq_ctx );
    if ( MFD_SUCCESS != *rc_ptr ) {
        Assert_assert( ( *rc_ptr == PRIOT_ERR_NOCREATION ) || ( *rc_ptr == PRIOT_ERR_INCONSISTENTNAME ) );
        ipCidrRouteTable_release_rowreq_ctx( rowreq_ctx );
        return NULL;
    }

    /*
     * copy indexes
     */
    rowreq_ctx->oid_idx.len = oid_idx->len;
    memcpy( rowreq_ctx->oid_idx.oids, oid_idx->oids,
        oid_idx->len * sizeof( oid ) );

    return rowreq_ctx;
} /* _mfd_ipCidrRouteTable_rowreq_from_index */

/**
 * @internal
 * wrapper
 */
static int
_mfd_ipCidrRouteTable_object_lookup( MibHandler* handler,
    HandlerRegistration* reginfo,
    AgentRequestInfo* agtreq_info,
    RequestInfo* requests )
{
    int rc = PRIOT_ERR_NOERROR;
    ipCidrRouteTable_rowreq_ctx* rowreq_ctx = ( ipCidrRouteTable_rowreq_ctx* )
        TableContainer_rowExtract( requests );

    DEBUG_MSGTL( ( "internal:ipCidrRouteTable:_mfd_ipCidrRouteTable_object_lookup", "called\n" ) );

    /*
     * get our context from mfd
     * ipCidrRouteTable_interface_ctx *if_ctx =
     *             (ipCidrRouteTable_interface_ctx *)reginfo->my_reg_void;
     */

    if ( NULL == rowreq_ctx ) {
        TableRequestInfo* tblreq_info;
        Types_Index oid_idx;

        tblreq_info = Table_extractTableInfo( requests );
        if ( NULL == tblreq_info ) {
            Logger_log( LOGGER_PRIORITY_ERR, "request had no table info\n" );
            return MFD_ERROR;
        }

        /*
         * try create rowreq
         */
        oid_idx.oids = tblreq_info->index_oid;
        oid_idx.len = tblreq_info->index_oid_len;

        rowreq_ctx = _mfd_ipCidrRouteTable_rowreq_from_index( &oid_idx, &rc );
        if ( MFD_SUCCESS == rc ) {
            Assert_assert( NULL != rowreq_ctx );
            rowreq_ctx->rowreq_flags |= MFD_ROW_CREATED;
            /*
             * add rowreq_ctx to request data lists
             */
            TableContainer_rowInsert( requests, ( Types_Index* )
                                                    rowreq_ctx );
        }
    }

    if ( MFD_SUCCESS != rc )
        Agent_requestSetErrorAll( requests, rc );
    else
        ipCidrRouteTable_row_prep( rowreq_ctx );

    return PRIOT_VALIDATE_ERR( rc );
} /* _mfd_ipCidrRouteTable_object_lookup */

