int _mfd_ipCidrRouteTable_get_values( MibHandler* handler,
    HandlerRegistration* reginfo,
    AgentRequestInfo* agtreq_info,
    RequestInfo* requests )
{
    ipCidrRouteTable_rowreq_ctx* rowreq_ctx = ( ipCidrRouteTable_rowreq_ctx* )
        TableContainer_rowExtract( requests );
    TableRequestInfo* tri;
    u_char* old_string;
    void ( *dataFreeHook )( void* );
    int rc;

    DEBUG_MSGTL( ( "internal:ipCidrRouteTable:_mfd_ipCidrRouteTable_get_values", "called\n" ) );

    Assert_assert( NULL != rowreq_ctx );

    for ( ; requests; requests = requests->next ) {
        /*
         * save old pointer, so we can free it if replaced
         */
        old_string = requests->requestvb->val.string;
        dataFreeHook = requests->requestvb->dataFreeHook;
        if ( NULL == requests->requestvb->val.string ) {
            requests->requestvb->val.string = requests->requestvb->buf;
            requests->requestvb->valLen = sizeof( requests->requestvb->buf );
        } else if ( requests->requestvb->buf == requests->requestvb->val.string ) {
            if ( requests->requestvb->valLen != sizeof( requests->requestvb->buf ) )
                requests->requestvb->valLen = sizeof( requests->requestvb->buf );
        }

        /*
         * get column data
         */
        tri = Table_extractTableInfo( requests );
        if ( NULL == tri )
            continue;

        rc = _ipCidrRouteTable_get_column( rowreq_ctx, requests->requestvb,
            tri->colnum );
        if ( rc ) {
            if ( MFD_SKIP == rc ) {
                requests->requestvb->type = PRIOT_NOSUCHINSTANCE;
                rc = PRIOT_ERR_NOERROR;
            }
        } else if ( NULL == requests->requestvb->val.string ) {
            Logger_log( LOGGER_PRIORITY_ERR, "NULL varbind data pointer!\n" );
            rc = ErrorCode_GENERR;
        }
        if ( rc )
            Agent_requestSetError( requests, PRIOT_VALIDATE_ERR( rc ) );

        /*
         * if the buffer wasn't used previously for the old data (i.e. it
         * was allcoated memory)  and the get routine replaced the pointer,
         * we need to free the previous pointer.
         */
        if ( old_string && ( old_string != requests->requestvb->buf ) && ( requests->requestvb->val.string != old_string ) ) {
            if ( dataFreeHook )
                ( *dataFreeHook )( old_string );
            else
                free( old_string );
        }
    } /* for results */

    return PRIOT_ERR_NOERROR;
} /* _mfd_ipCidrRouteTable_get_values */

static inline int
_ipCidrRouteTable_check_indexes( ipCidrRouteTable_rowreq_ctx* rowreq_ctx )
{
    int rc = ErrorCode_SUCCESS;

    DEBUG_MSGTL( ( "internal:ipCidrRouteTable:_ipCidrRouteTable_check_indexes", "called\n" ) );

    Assert_assert( NULL != rowreq_ctx );

    /*
     * (INDEX) ipCidrRouteDest(1)/IPADDR/ASN_IPADDRESS/u_long(u_long)//l/A/w/e/r/d/h 
     */
    if ( MFD_SUCCESS != rc )
        return rc;
    rc = ipCidrRouteDest_check_index( rowreq_ctx );
    if ( MFD_SUCCESS != rc )
        return PRIOT_ERR_NOCREATION;

    /*
     * (INDEX) ipCidrRouteMask(2)/IPADDR/ASN_IPADDRESS/u_long(u_long)//l/A/w/e/r/d/h 
     */
    if ( MFD_SUCCESS != rc )
        return rc;
    rc = ipCidrRouteMask_check_index( rowreq_ctx );
    if ( MFD_SUCCESS != rc )
        return PRIOT_ERR_NOCREATION;

    /*
     * (INDEX) ipCidrRouteTos(3)/INTEGER32/ASN_INTEGER/long(long)//l/A/w/e/R/d/h 
     */
    /*
     * check defined range(s). 
     */
    if ( ( ErrorCode_SUCCESS == rc )
        && ( ( rowreq_ctx->tbl_idx.ipCidrRouteTos < 0 )
               || ( rowreq_ctx->tbl_idx.ipCidrRouteTos > 2147483647 ) ) ) {
        rc = PRIOT_ERR_WRONGVALUE;
    }
    if ( MFD_SUCCESS != rc )
        return rc;
    rc = ipCidrRouteTos_check_index( rowreq_ctx );
    if ( MFD_SUCCESS != rc )
        return PRIOT_ERR_NOCREATION;

    /*
     * (INDEX) ipCidrRouteNextHop(4)/IPADDR/ASN_IPADDRESS/u_long(u_long)//l/A/w/e/r/d/h 
     */
    if ( MFD_SUCCESS != rc )
        return rc;
    rc = ipCidrRouteNextHop_check_index( rowreq_ctx );
    if ( MFD_SUCCESS != rc )
        return PRIOT_ERR_NOCREATION;

    /*
     * if individual parts look ok, check them as a whole
     */
    return ipCidrRouteTable_validate_index( ipCidrRouteTable_if_ctx.user_ctx, rowreq_ctx );
} /* _ipCidrRouteTable_check_indexes */

