/**
 * @internal
 * undo setup
 */
int _mfd_ipCidrRouteTable_undo_setup( MibHandler* handler,
    HandlerRegistration* reginfo,
    AgentRequestInfo* agtreq_info,
    RequestInfo* requests )
{
    int rc;
    ipCidrRouteTable_rowreq_ctx* rowreq_ctx = ( ipCidrRouteTable_rowreq_ctx* )
        TableContainer_rowExtract( requests );

    DEBUG_MSGTL( ( "internal:ipCidrRouteTable:_mfd_ipCidrRouteTable_undo_setup", "called\n" ) );

    Assert_assert( NULL != rowreq_ctx );

    /*
     * allocate undo context
     */
    rowreq_ctx->undo = ipCidrRouteTable_allocate_data();
    if ( NULL == rowreq_ctx->undo ) {
        /** msg already logged */
        Agent_requestSetErrorAll( requests,
            PRIOT_ERR_RESOURCEUNAVAILABLE );
        return PRIOT_ERR_NOERROR;
    }

    /*
     * row undo setup
     */
    rowreq_ctx->column_set_flags = 0;
    rc = ipCidrRouteTable_undo_setup( rowreq_ctx );
    if ( MFD_SUCCESS != rc ) {
        DEBUG_MSGTL( ( "ipCidrRouteTable:mfd", "error %d from "
                                               "ipCidrRouteTable_undo_setup\n",
            rc ) );
        Agent_requestSetErrorAll( requests, PRIOT_VALIDATE_ERR( rc ) );
    } else {
        /*
         * column undo setup
         */
        TableRequestInfo* tri;
        for ( ; requests; requests = requests->next ) {
            /*
             * set column data
             */
            tri = Table_extractTableInfo( requests );
            if ( NULL == tri )
                continue;

            rc = _ipCidrRouteTable_undo_setup_column( rowreq_ctx,
                tri->colnum );
            if ( MFD_SUCCESS != rc ) {
                DEBUG_MSGTL( ( "ipCidrRouteTable:mfd", "error %d from "
                                                       "ipCidrRouteTable_undo_setup_column\n",
                    rc ) );
                Agent_setRequestError( agtreq_info, requests,
                    PRIOT_VALIDATE_ERR( rc ) );
            }
        } /* for results */
    }

    return PRIOT_ERR_NOERROR;
} /* _mfd_ipCidrRouteTable_undo_setup */

/**
 * @internal
 * undo setup
 */
int _mfd_ipCidrRouteTable_undo_cleanup( MibHandler* handler,
    HandlerRegistration* reginfo,
    AgentRequestInfo* agtreq_info,
    RequestInfo* requests )
{
    ipCidrRouteTable_rowreq_ctx* rowreq_ctx = ( ipCidrRouteTable_rowreq_ctx* )
        TableContainer_rowExtract( requests );
    int rc;

    DEBUG_MSGTL( ( "internal:ipCidrRouteTable:_mfd_ipCidrRouteTable_undo_cleanup", "called\n" ) );

    /*
     * failed row create in early stages has no rowreq_ctx
     */
    if ( NULL == rowreq_ctx )
        return MFD_SUCCESS;

    /*
     * call user cleanup
     */
    rc = ipCidrRouteTable_undo_cleanup( rowreq_ctx );
    if ( MFD_SUCCESS != rc ) {
        /*
         * nothing we can do about it but log it
         */
        DEBUG_MSGTL( ( "ipCidrRouteTable:mfd", "error %d from "
                                               "ipCidrRouteTable_undo_cleanup\n",
            rc ) );
    }

    /*
     * release undo context, if needed
     */
    if ( rowreq_ctx->undo ) {
        ipCidrRouteTable_release_data( rowreq_ctx->undo );
        rowreq_ctx->undo = NULL;
    }

    return PRIOT_ERR_NOERROR;
} /* _mfd_ipCidrRouteTable_undo_cleanup */

