/*----------------------------------------------------------------------
 *
 * SET: irreversible commit
 *
 *---------------------------------------------------------------------*/
/**
 * @internal
 * commit irreversible actions
 */
int _mfd_inetNetToMediaTable_irreversible_commit( MibHandler* handler, HandlerRegistration* reginfo, AgentRequestInfo* agtreq_info, RequestInfo* requests )
{
    inetNetToMediaTable_rowreq_ctx* rowreq_ctx = ( inetNetToMediaTable_rowreq_ctx* )
        TableContainer_rowExtract( requests );

    DEBUG_MSGTL( ( "internal:inetNetToMediaTable:_mfd_inetNetToMediaTable_irreversible:commit", "called\n" ) );

    Assert_assert( NULL != rowreq_ctx );

    /*
     * check for and handle row creation/deletion
     * and update column exist flags...
     */
    if ( rowreq_ctx->rowreq_flags & MFD_ROW_DELETED ) {
        if ( !( rowreq_ctx->rowreq_flags & MFD_ROW_CREATED ) )
            CONTAINER_REMOVE( inetNetToMediaTable_if_ctx.container, rowreq_ctx );
    } else {
        if ( rowreq_ctx->column_set_flags ) {
            rowreq_ctx->column_set_flags = 0;
        }
        if ( rowreq_ctx->rowreq_flags & MFD_ROW_CREATED ) {
            rowreq_ctx->rowreq_flags &= ~MFD_ROW_CREATED;
            CONTAINER_INSERT( inetNetToMediaTable_if_ctx.container,
                rowreq_ctx );
        }
    }

    return PRIOT_ERR_NOERROR;
} /* _mfd_inetNetToMediaTable_irreversible_commit */

