int _mfd_ipAddressTable_get_values( MibHandler* handler,
    HandlerRegistration* reginfo,
    AgentRequestInfo* agtreq_info,
    RequestInfo* requests )
{
    ipAddressTable_rowreq_ctx* rowreq_ctx = ( ipAddressTable_rowreq_ctx* )
        TableContainer_rowExtract( requests );
    TableRequestInfo* tri;
    u_char* old_string;
    void ( *dataFreeHook )( void* );
    int rc;

    DEBUG_MSGTL( ( "internal:ipAddressTable:_mfd_ipAddressTable_get_values",
        "called\n" ) );

    Assert_assert( NULL != rowreq_ctx );

    for ( ; requests; requests = requests->next ) {
        /*
         * save old pointer, so we can free it if replaced
         */
        old_string = requests->requestvb->value.string;
        dataFreeHook = requests->requestvb->dataFreeHook;
        if ( NULL == requests->requestvb->value.string ) {
            requests->requestvb->value.string = requests->requestvb->buffer;
            requests->requestvb->valueLength = sizeof( requests->requestvb->buffer );
        } else if ( requests->requestvb->buffer == requests->requestvb->value.string ) {
            if ( requests->requestvb->valueLength != sizeof( requests->requestvb->buffer ) )
                requests->requestvb->valueLength = sizeof( requests->requestvb->buffer );
        }

        /*
         * get column data
         */
        tri = Table_extractTableInfo( requests );
        if ( NULL == tri )
            continue;

        rc = _ipAddressTable_get_column( rowreq_ctx, requests->requestvb,
            tri->colnum );
        if ( rc ) {
            if ( MFD_SKIP == rc ) {
                requests->requestvb->type = PRIOT_NOSUCHINSTANCE;
                rc = PRIOT_ERR_NOERROR;
            }
        } else if ( NULL == requests->requestvb->value.string ) {
            Logger_log( LOGGER_PRIORITY_ERR, "NULL varbind data pointer!\n" );
            rc = PRIOT_ERR_GENERR;
        }
        if ( rc )
            Agent_requestSetError( requests, PRIOT_VALIDATE_ERR( rc ) );

        /*
         * if the buffer wasn't used previously for the old data (i.e. it
         * was allcoated memory)  and the get routine replaced the pointer,
         * we need to free the previous pointer.
         */
        if ( old_string && ( old_string != requests->requestvb->buffer ) && ( requests->requestvb->value.string != old_string ) ) {
            if ( dataFreeHook )
                ( *dataFreeHook )( old_string );
            else
                free( old_string );
        }
    } /* for results */

    return PRIOT_ERR_NOERROR;
} /* _mfd_ipAddressTable_get_values */

static inline int
_ipAddressTable_check_indexes( ipAddressTable_rowreq_ctx* rowreq_ctx )
{
    int rc = ErrorCode_SUCCESS;

    DEBUG_MSGTL( ( "internal:ipAddressTable:_ipAddressTable_check_indexes",
        "called\n" ) );

    Assert_assert( NULL != rowreq_ctx );

    /*
     * (INDEX) ipAddressAddrType(1)/InetAddressType/ASN_INTEGER/long(u_long)//l/a/w/E/r/d/h 
     */
    /*
     * check that the value is one of defined enums 
     */
    if ( ( ErrorCode_SUCCESS == rc )
        && ( rowreq_ctx->tbl_idx.ipAddressAddrType != INETADDRESSTYPE_UNKNOWN )
        && ( rowreq_ctx->tbl_idx.ipAddressAddrType != INETADDRESSTYPE_IPV4 )
        && ( rowreq_ctx->tbl_idx.ipAddressAddrType != INETADDRESSTYPE_IPV6 )
        && ( rowreq_ctx->tbl_idx.ipAddressAddrType != INETADDRESSTYPE_IPV4Z )
        && ( rowreq_ctx->tbl_idx.ipAddressAddrType != INETADDRESSTYPE_IPV6Z )
        && ( rowreq_ctx->tbl_idx.ipAddressAddrType != INETADDRESSTYPE_DNS ) ) {
        rc = PRIOT_ERR_WRONGVALUE;
    }
    if ( MFD_SUCCESS != rc )
        return rc;
    rc = ipAddressAddrType_check_index( rowreq_ctx );
    if ( MFD_SUCCESS != rc )
        return PRIOT_ERR_NOCREATION;

    /*
     * (INDEX) ipAddressAddr(2)/InetAddress/ASN_OCTET_STR/char(char)//L/a/w/e/R/d/h 
     */
    /*
     * check defined range(s). 
     */
    if ( ( ErrorCode_SUCCESS == rc )
        && ( ( rowreq_ctx->tbl_idx.ipAddressAddr_len < 0 )
               || ( rowreq_ctx->tbl_idx.ipAddressAddr_len > 255 ) ) ) {
        rc = PRIOT_ERR_WRONGLENGTH;
    }
    if ( MFD_SUCCESS != rc )
        return rc;
    rc = ipAddressAddr_check_index( rowreq_ctx );
    if ( MFD_SUCCESS != rc )
        return PRIOT_ERR_NOCREATION;

    /*
     * if individual parts look ok, check them as a whole
     */
    return ipAddressTable_validate_index( ipAddressTable_if_ctx.user_ctx,
        rowreq_ctx );
} /* _ipAddressTable_check_indexes */

