/*----------------------------------------------------------------------
 *
 * SET: irreversible commit
 *
 *---------------------------------------------------------------------*/
/**
 * @internal
 * commit irreversible actions
 */
int _mfd_ipAddressTable_irreversible_commit( MibHandler* handler, HandlerRegistration* reginfo, AgentRequestInfo* agtreq_info,
    RequestInfo* requests )
{
    ipAddressTable_rowreq_ctx* rowreq_ctx = ( ipAddressTable_rowreq_ctx* )
        TableContainer_rowExtract( requests );

    DEBUG_MSGTL( ( "internal:ipAddressTable:_mfd_ipAddressTable_irreversible:commit", "called\n" ) );

    Assert_assert( NULL != rowreq_ctx );

    /*
     * check for and handle row creation/deletion
     * and update column exist flags...
     */
    if ( rowreq_ctx->rowreq_flags & MFD_ROW_DELETED ) {
        if ( !( rowreq_ctx->rowreq_flags & MFD_ROW_CREATED ) )
            CONTAINER_REMOVE( ipAddressTable_if_ctx.container, rowreq_ctx );
    } else {
        if ( rowreq_ctx->column_set_flags ) {
            rowreq_ctx->column_set_flags = 0;
        }
        if ( rowreq_ctx->rowreq_flags & MFD_ROW_CREATED ) {
            rowreq_ctx->rowreq_flags &= ~MFD_ROW_CREATED;
            CONTAINER_INSERT( ipAddressTable_if_ctx.container, rowreq_ctx );
        }
    }

    return PRIOT_ERR_NOERROR;
} /* _mfd_ipAddressTable_irreversible_commit */

/***********************************************************************
 *
 * DATA ACCESS
 *
 ***********************************************************************/
static void _container_free( Container_Container* container );

/**
 * @internal
 */
static int
_cache_load( Cache* cache, void* vmagic )
{
    DEBUG_MSGTL( ( "internal:ipAddressTable:_cache_load", "called\n" ) );

    if ( ( NULL == cache ) || ( NULL == cache->magic ) ) {
        Logger_log( LOGGER_PRIORITY_ERR, "invalid cache for ipAddressTable_cache_load\n" );
        return -1;
    }

    /** should only be called for an invalid or expired cache */
    Assert_assert( ( 0 == cache->valid ) || ( 1 == cache->expired ) );

    /*
     * call user code
     */
    return ipAddressTable_container_load( ( Container_Container* )cache->magic );
} /* _cache_load */

/**
 * @internal
 */
static void
_cache_free( Cache* cache, void* magic )
{
    Container_Container* container;

    DEBUG_MSGTL( ( "internal:ipAddressTable:_cache_free", "called\n" ) );

    if ( ( NULL == cache ) || ( NULL == cache->magic ) ) {
        Logger_log( LOGGER_PRIORITY_ERR, "invalid cache in ipAddressTable_cache_free\n" );
        return;
    }

    container = ( Container_Container* )cache->magic;

    _container_free( container );
} /* _cache_free */

/**
 * @internal
 */
static void
_container_item_free( ipAddressTable_rowreq_ctx* rowreq_ctx, void* context )
{
    DEBUG_MSGTL( ( "internal:ipAddressTable:_container_item_free",
        "called\n" ) );

    if ( NULL == rowreq_ctx )
        return;

    ipAddressTable_release_rowreq_ctx( rowreq_ctx );
} /* _container_item_free */

/**
 * @internal
 */
static void
_container_free( Container_Container* container )
{
    DEBUG_MSGTL( ( "internal:ipAddressTable:_container_free", "called\n" ) );

    if ( NULL == container ) {
        Logger_log( LOGGER_PRIORITY_ERR,
            "invalid container in ipAddressTable_container_free\n" );
        return;
    }

    /*
     * call user code
     */
    ipAddressTable_container_free( container );

    /*
     * free all items. inefficient, but easy.
     */
    CONTAINER_CLEAR( container,
        ( Container_FuncObjFunc* )_container_item_free,
        NULL );
} /* _container_free */

/**
 * @internal
 * initialize the container with functions or wrappers
 */
void _ipAddressTable_container_init( ipAddressTable_interface_ctx* if_ctx )
{
    DEBUG_MSGTL( ( "internal:ipAddressTable:_ipAddressTable_container_init",
        "called\n" ) );

    /*
     * cache init
     */
    if_ctx->cache = CacheHandler_create( 30, /* timeout in seconds */
        _cache_load, _cache_free,
        ipAddressTable_oid,
        ipAddressTable_oid_size );

    if ( NULL == if_ctx->cache ) {
        Logger_log( LOGGER_PRIORITY_ERR, "error creating cache for ipAddressTable\n" );
        return;
    }

    if_ctx->cache->flags = CacheOperation_DONT_INVALIDATE_ON_SET;

    ipAddressTable_container_init( &if_ctx->container, if_ctx->cache );
    if ( NULL == if_ctx->container )
        if_ctx->container = Container_find( "ipAddressTable:tableContainer" );
    if ( NULL == if_ctx->container ) {
        Logger_log( LOGGER_PRIORITY_ERR, "error creating container in "
                                         "ipAddressTable_container_init\n" );
        return;
    }

    if ( NULL != if_ctx->cache )
        if_ctx->cache->magic = ( void* )if_ctx->container;
} /* _ipAddressTable_container_init */

/**
 * @internal
 * shutdown the container with functions or wrappers
 */
void _ipAddressTable_container_shutdown( ipAddressTable_interface_ctx* if_ctx )
{
    DEBUG_MSGTL( ( "internal:ipAddressTable:_ipAddressTable_container_shutdown", "called\n" ) );

    ipAddressTable_container_shutdown( if_ctx->container );

    _container_free( if_ctx->container );

} /* _ipAddressTable_container_shutdown */

ipAddressTable_rowreq_ctx*
ipAddressTable_row_find_by_mib_index( ipAddressTable_mib_index* mib_idx )
{
    ipAddressTable_rowreq_ctx* rowreq_ctx;
    oid oid_tmp[ asnMAX_OID_LEN ];
    Types_Index oid_idx;
    int rc;

    /*
     * set up storage for OID
     */
    oid_idx.oids = oid_tmp;
    oid_idx.len = sizeof( oid_tmp ) / sizeof( oid );

    /*
     * convert
     */
    rc = ipAddressTable_index_to_oid( &oid_idx, mib_idx );
    if ( MFD_SUCCESS != rc )
        return NULL;

    rowreq_ctx = ( ipAddressTable_rowreq_ctx* )CONTAINER_FIND( ipAddressTable_if_ctx.container, &oid_idx );

    return rowreq_ctx;
}
