static void _container_free( Container_Container* container );

/**
 * @internal
 */
static int
_cache_load( Cache* cache, void* vmagic )
{
    DEBUG_MSGTL( ( "internal:ipSystemStatsTable:_cache_load", "called\n" ) );

    if ( ( NULL == cache ) || ( NULL == cache->magic ) ) {
        Logger_log( LOGGER_PRIORITY_ERR,
            "invalid cache for ipSystemStatsTable_cache_load\n" );
        return -1;
    }

    /** should only be called for an invalid or expired cache */
    Assert_assert( ( 0 == cache->valid ) || ( 1 == cache->expired ) );

    /*
     * call user code
     */
    return ipSystemStatsTable_container_load( ( Container_Container* )cache->magic );
} /* _cache_load */

/**
 * @internal
 */
static void
_cache_free( Cache* cache, void* magic )
{
    Container_Container* container;

    DEBUG_MSGTL( ( "internal:ipSystemStatsTable:_cache_free", "called\n" ) );

    if ( ( NULL == cache ) || ( NULL == cache->magic ) ) {
        Logger_log( LOGGER_PRIORITY_ERR,
            "invalid cache in ipSystemStatsTable_cache_free\n" );
        return;
    }

    container = ( Container_Container* )cache->magic;

    _container_free( container );
} /* _cache_free */

/**
 * @internal
 */
static void
_container_item_free( ipSystemStatsTable_rowreq_ctx* rowreq_ctx,
    void* context )
{
    DEBUG_MSGTL( ( "internal:ipSystemStatsTable:_container_item_free",
        "called\n" ) );

    if ( NULL == rowreq_ctx )
        return;

    ipSystemStatsTable_release_rowreq_ctx( rowreq_ctx );
} /* _container_item_free */

/**
 * @internal
 */
static void
_container_free( Container_Container* container )
{
    DEBUG_MSGTL( ( "internal:ipSystemStatsTable:_container_free",
        "called\n" ) );

    if ( NULL == container ) {
        Logger_log( LOGGER_PRIORITY_ERR,
            "invalid container in ipSystemStatsTable_container_free\n" );
        return;
    }

    /*
     * call user code
     */
    ipSystemStatsTable_container_free( container );

    /*
     * free all items. inefficient, but easy.
     */
    CONTAINER_CLEAR( container,
        ( Container_FuncObjFunc* )_container_item_free,
        NULL );
} /* _container_free */

/**
 * @internal
 * initialize the container with functions or wrappers
 */
void _ipSystemStatsTable_container_init( ipSystemStatsTable_interface_ctx*
        if_ctx )
{
    DEBUG_MSGTL( ( "internal:ipSystemStatsTable:_ipSystemStatsTable_container_init", "called\n" ) );

    /*
     * cache init
     */
    if_ctx->cache = CacheHandler_create( 30, /* timeout in seconds */
        _cache_load, _cache_free,
        ipSystemStatsTable_oid,
        ipSystemStatsTable_oid_size );

    if ( NULL == if_ctx->cache ) {
        Logger_log( LOGGER_PRIORITY_ERR, "error creating cache for ipSystemStatsTable\n" );
        return;
    }

    if_ctx->cache->flags = CacheOperation_DONT_INVALIDATE_ON_SET;

    ipSystemStatsTable_container_init( &if_ctx->container, if_ctx->cache );
    if ( NULL == if_ctx->container )
        if_ctx->container = Container_find( "ipSystemStatsTable:tableContainer" );
    if ( NULL == if_ctx->container ) {
        Logger_log( LOGGER_PRIORITY_ERR, "error creating container in "
                                         "ipSystemStatsTable_container_init\n" );
        return;
    }

    if ( NULL != if_ctx->cache )
        if_ctx->cache->magic = ( void* )if_ctx->container;
} /* _ipSystemStatsTable_container_init */

/**
 * @internal
 * shutdown the container with functions or wrappers
 */
void _ipSystemStatsTable_container_shutdown( ipSystemStatsTable_interface_ctx*
        if_ctx )
{
    DEBUG_MSGTL( ( "internal:ipSystemStatsTable:_ipSystemStatsTable_container_shutdown", "called\n" ) );

    ipSystemStatsTable_container_shutdown( if_ctx->container );

    _container_free( if_ctx->container );

} /* _ipSystemStatsTable_container_shutdown */

ipSystemStatsTable_rowreq_ctx*
ipSystemStatsTable_row_find_by_mib_index( ipSystemStatsTable_mib_index*
        mib_idx )
{
    ipSystemStatsTable_rowreq_ctx* rowreq_ctx;
    oid oid_tmp[ asnMAX_OID_LEN ];
    Types_Index oid_idx;
    int rc;

    /*
     * set up storage for OID
     */
    oid_idx.oids = oid_tmp;
    oid_idx.len = sizeof( oid_tmp ) / sizeof( oid );

    /*
     * convert
     */
    rc = ipSystemStatsTable_index_to_oid( &oid_idx, mib_idx );
    if ( MFD_SUCCESS != rc )
        return NULL;

    rowreq_ctx = ( ipSystemStatsTable_rowreq_ctx* )
        CONTAINER_FIND( ipSystemStatsTable_if_ctx.container, &oid_idx );

    return rowreq_ctx;
}
