static int _snmpNotifyFilterTable_container_save_rows( int majorID,
    int minorID,
    void* serverarg,
    void* clientarg );
static void _snmpNotifyFilterTable_container_row_restore( const char* token,
    char* buf );
static int
_snmpNotifyFilterTable_container_row_save( snmpNotifyFilterTable_rowreq_ctx* rowreq_ctx,
    void* type );
static char

    *
    _snmpNotifyFilterTable_container_col_restore( snmpNotifyFilterTable_rowreq_ctx* rowreq_ctx, u_int col, char* buf );
