    TableRequestInfo* tri;

    DEBUG_MSGTL( ( "internal:tcpConnectionTable:_mfd_tcpConnectionTable_undo_values", "called\n" ) );

    Assert_assert( NULL != rowreq_ctx );

    rc = tcpConnectionTable_undo( rowreq_ctx );
